CREATE DATABASE IF NOT EXISTS `parc_auto` DEFAULT CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci;
USE `parc_auto`;

-- Jeu de données parc_auto généré automatiquement
CREATE TABLE `assurance` ( 
  `id` INT UNSIGNED AUTO_INCREMENT NOT NULL,
  `numero` VARCHAR(50) NOT NULL,
  `assureur` VARCHAR(150) NOT NULL,
  `date_debut` DATE NOT NULL,
  `date_fin` DATE NOT NULL,
  `vehicule_id` INT UNSIGNED NOT NULL,
   PRIMARY KEY (`id`)
)
ENGINE = InnoDB;
CREATE TABLE `commune` ( 
  `id` INT UNSIGNED AUTO_INCREMENT NOT NULL,
  `nom` VARCHAR(150) NOT NULL,
   PRIMARY KEY (`id`)
)
ENGINE = InnoDB;
CREATE TABLE `contravention` ( 
  `id` INT UNSIGNED AUTO_INCREMENT NOT NULL,
  `date_contravention` DATE NOT NULL,
  `montant` DECIMAL(10,2) NOT NULL,
  `vehicule_id` INT UNSIGNED NOT NULL,
  `conducteur_id` INT UNSIGNED NULL,
  `lieu_id` INT UNSIGNED NULL,
   PRIMARY KEY (`id`)
)
ENGINE = InnoDB;
CREATE TABLE `entretien` ( 
  `id` INT UNSIGNED AUTO_INCREMENT NOT NULL,
  `vehicule_id` INT UNSIGNED NOT NULL,
  `garage_id` INT UNSIGNED NOT NULL,
  `date_entretien` DATE NOT NULL,
  `description` VARCHAR(255) NOT NULL,
  `cout` DECIMAL(10,2) NOT NULL,
   PRIMARY KEY (`id`)
)
ENGINE = InnoDB;
CREATE TABLE `garage` ( 
  `id` INT UNSIGNED AUTO_INCREMENT NOT NULL,
  `nom` VARCHAR(150) NOT NULL,
  `commune_id` INT UNSIGNED NULL,
   PRIMARY KEY (`id`)
)
ENGINE = InnoDB;
CREATE TABLE `personne` ( 
  `id` INT UNSIGNED AUTO_INCREMENT NOT NULL,
  `nom` VARCHAR(100) NOT NULL,
  `prenom` VARCHAR(100) NOT NULL,
  `tel` VARCHAR(20) NULL,
   PRIMARY KEY (`id`)
)
ENGINE = InnoDB;
CREATE TABLE `vehicule` ( 
  `id` INT UNSIGNED AUTO_INCREMENT NOT NULL,
  `immatriculation` VARCHAR(15) NOT NULL,
  `marque` VARCHAR(100) NOT NULL,
  `modele` VARCHAR(100) NOT NULL,
  `energie` VARCHAR(50) NOT NULL,
  `annee` SMALLINT NOT NULL,
  `proprietaire_id` INT UNSIGNED NOT NULL,
   PRIMARY KEY (`id`),
  CONSTRAINT `uq_vehicule_immat` UNIQUE (`immatriculation`)
)
ENGINE = InnoDB;

